mapper.select:
SELECT id, phone, address, email
FROM users_f5 
WHERE id = #{id}

mapper.delete:
DELETE FROM cart_f5
WHERE product_code IN (SELECT product_code FROM order_detail_f5 WHERE order_code = #{orderCode})
AND id = #{id}

mapper.update:
UPDATE order_f5
SET order_status = #{status}
WHERE order_code = #{orderCode}
