mapper.select:
select count(1)
from   qna_f5
where  qna_subject = #{qnaSubject}

mapper.update:
update qna_f5
set    qna_subject = #{qnaSubject},
       qna_content = #{qnaContent}
where  qna_code = #{qnaCode}

mapper.select:
		select *
		from   qna_f5
        where  product_code like #{productCode}
