mapper.select:
select count(1)
from   review_f5
where  review_subject = #{reviewSubject}

mapper.update:
update review_f5
set    review_subject = #{reviewSubject},
       review_content = #{reviewContent}
where  review_code = #{reviewCode}

mapper.select:
select *
from   review_f5
where  product_code like #{productCode}
